class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      authUser: null,
    };
  }

  componentDidMount() {
      // when the user's authentication status changes, update the state
      firebase.auth.onAuthStateChanged( authUser => { authUser ? this.setState({authUser}) : this.setState({authUser: null}); } );
  }

  render() {
    return (
            <Router>
                <div>
                    <Page authUser={this.state.authUser} />
                </div>
            </Router>
    );
  }
}

export default App;
